import React from "react";
import { ProductCard } from "./index.js";
import arrow from "../assets/arrow.svg";
import { Link } from "react-router-dom";

function CategoryMenu({
  categories,
  products,
  changeCategory,
  changeSelectItem,
}) {
  return (
    <div className="category-menu">
      <div className="category-menu__categories">
        <div>
          <h1>Best selling products</h1>
          <div>
            {categories.map((c, i) => (
              <p onClick={() => changeCategory(c)} key={i}>
                {c.charAt(0).toUpperCase() + c.slice(1)}
              </p>
            ))}
          </div>
        </div>
        <Link to="/freeshnesecom/products">
          <button className="category-menu__btn">
            More products <img src={arrow} alt="arrow" />
          </button>
        </Link>
      </div>
      <div className="category-menu__products">
        {products.slice(0, 3).map((product, index) => (
          <Link
            onClick={() => changeSelectItem(product)}
            key={index}
            to={'/freeshnesecom/products/item/' + product.id}
          >
            <ProductCard obj={product} />
          </Link>
        ))}
      </div>
    </div>
  );
}

export default CategoryMenu;
